import { Component } from "@angular/core";
import { CommonModule } from "@angular/common";

interface Project {
  title: string;
  description: string;
  technologies: string[];
  image: string;
  link?: string;
}

@Component({
  selector: "app-projects",
  standalone: true,
  imports: [CommonModule],
  template: `
    <section id="projects" class="projects">
      <div class="container">
        <h2 class="section-title">My Projects</h2>
        <p class="section-subtitle">Here are some of the projects I've built</p>
        <div class="projects-grid">
          <div *ngFor="let project of projects" class="project-card">
            <div class="project-image">
              <img [src]="project.image" [alt]="project.title" (error)="handleImageError($event)">
              <div class="project-overlay">
                <a *ngIf="project.link" [href]="project.link" target="_blank" class="project-link">View Project</a>
              </div>
            </div>
            <div class="project-content">
              <h3>{{ project.title }}</h3>
              <p>{{ project.description }}</p>
              <div class="project-technologies">
                <span *ngFor="let tech of project.technologies" class="tech-tag">{{ tech }}</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  `,
  styles: [
    `
    /* Your existing styles remain unchanged */
    .projects {
      padding: 100px 0;
      background: white;
    }

    /* ... rest of your styles ... */
    `
  ]
})
export class ProjectsComponent {
  projects: Project[] = [
    {
      title: "Rim-Commerce Platform",
      description: "A full-stack Rim-commerce solution with user authentication, product management, and payment integration.",
      technologies: ["Angular", "Node.js", "MongoDB", "Stripe"],
      image: "assets/images/1.jpeg",
      link: "#"
    },
    {
      title: "Task Management App",
      description: "A collaborative task management application with real-time updates and team collaboration features.",
      technologies: ["React", "Firebase", "Material-UI", "WebSocket"],
      image: "assets/images/2.jpeg", // Changed from placeholder
      link: "#"
    },
    {
      title: "Weather Dashboard",
      description: "A responsive weather application with location-based forecasts and interactive charts.",
      technologies: ["Vue.js", "Chart.js", "OpenWeather API", "CSS3"],
      image: "assets/images/3.jpeg", // Changed from placeholder
      link: "#"
    }
    // Add more projects as needed
  ];

  handleImageError(event: Event) {
    const imgElement = event.target as HTMLImageElement;
    imgElement.src = 'assets/images/default-project.jpg'; // Fallback image
  }
}
